INSERT INTO `story` (`StoryID`, `StoryName`, `StorySlug`, `AuthorID`, `CategoryID`, `Descrition`, `PublishedDate`, `Img`, `view`, `Favourite`, `Follow`, `Rating`) VALUES
(5, 'Đại Phụng Bại Gia Tử', '', 5, 5, 'Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ hay online. Truyện Đại Phụng Bại Gia Tử là một truyện chữ hay của tác giả Mạn Bộ Phong Trung sáng tác thuộc thể loại Dã sử, Cơ Trí, Giá Không Lịch Sử, Xuyên Qua, với diễn biến, nội dung truyện rất hấp dẫn và cuốn hút. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới đầy sự sáng tạo mới lạ, những tình tiết đặc sắc, giúp bạn có những trải nghiệm thích thú, mới lạ hơn.\n\nTruyện Chữ Truyện Đại Phụng Bại Gia Tử được cập nhật nhanh và đầy đủ nhất tại TruyenKMA. Bạn đọc đừng quên để lại bình luận và chia sẻ, ủng hộ TruyenKMA ra các chương mới nhất của truyện Truyện Đại Phụng Bại Gia Tử.', '2025-11-28 05:17:36', 'DaiPhungBaiGiaTu.jpg', 15, 0, 1, 4),
(6, 'Quái Vật Tới Rồi', '', 6, 6, 'Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ hay online. Truyện Quái Vật Tới Rồi là một truyenchu của tác giả Nhất Đao Trảm Trảm Trảm sáng tác thuộc thể loại Võng du, Hệ Thống, Điềm Đạm, Thế Giới Tương Lai, với diễn biến, nội dung truyện rất hấp dẫn và cuốn hút. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới đầy sự sáng tạo mới lạ, những tình tiết đặc sắc, giúp bạn có những trải nghiệm thích thú, mới lạ hơn.\n\nTruyện Chữ Truyện Quái Vật Tới Rồi được cập nhật nhanh và đầy đủ nhất tại TruyenChu. Bạn đọc đừng quên để lại bình luận và chia sẻ, ủng hộ TruyenChu ra các chương mới nhất của truyện Truyện Quái Vật Tới Rồi.', '2025-11-28 08:22:31', 'QuaiVatToiRoi.jpg', 0, 0, 0, 0),
(7, 'Vấn Thiên Tam Tội', '', 7, 7, 'Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ hay online. Truyện Vấn Thiên Tam Tội là một truyện hay online của tác giả 4035 sáng tác thuộc thể loại Huyền huyễn, Lãnh Khốc, Đông Phương Huyền Huyễn, với diễn biến, nội dung truyện rất hấp dẫn và cuốn hút. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới đầy sự sáng tạo mới lạ, những tình tiết đặc sắc, giúp bạn có những trải nghiệm thích thú, mới lạ hơn.\n\nTruyện Chữ Truyện Vấn Thiên Tam Tội được cập nhật nhanh và đầy đủ nhất tại TruyenChu. Bạn đọc đừng quên để lại bình luận và chia sẻ, ủng hộ TruyenChu ra các chương mới nhất của truyện Truyện Vấn Thiên Tam Tội.', '2025-11-28 08:26:22', 'VanThienTamToi.jpg', 0, 0, 0, 0),
(8, 'Phàm Nhân Tiên Hồ', '', 8, 7, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\nNhị Cẩu Tử cả đời mơ ước lớn nhất chính là tích lũy vài mẫu ruộng, làm cái tiểu địa chủ, lại cưới cái eo thô chân to nàng dâu, đời người liền hoàn mỹ.\n\nLại bởi vì ngoài ý muốn được đến một cái hồ lô, lại bị lão già lừa đảo lắc lư, không cẩn thận bước vào tiên đạo, bất đắc dĩ cuốn vào phân tranh, nhưng hắn sơ tâm không thay đổi, chỉ muốn làm ruộng……', '2025-11-28 08:30:32', 'PhamNhanTienHo.jpg', 0, 0, 0, 0),
(9, 'Cấm Kỵ Thần Vương', '', 9, 7, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\nSở Gia Thiên Mệnh Kỳ Lân Tử, tuổi nhỏ bị người lấy một chén rượu độc, hủy đi thiên phú, biến thành đại giáo khí đồ! Hắn dấn thân vào quân lữ, chìm nổi sáu năm sau, được tôn xưng Bắc Vương', '2025-11-28 08:32:29', 'CamKyThanVuong.jpg', 0, 0, 0, 0),
(10, 'Tẫn Thiên Hành Giả', '', 10, 7, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\nTa nương theo lấy phong kị vật tỉnh lại, nhận thức đến đây là một cái vận mệnh cùng quỷ bí xen lẫn thế giới', '2025-11-28 08:34:15', 'TanThienHanhGia.jpg', 0, 0, 0, 0),
(11, 'Vong Linh Hơi Thở', '', 11, 8, 'Văn án truyện:\nNhậm chức Vong Linh Pháp Sư lúc, Cố Tức phát hiện chính mình gặp một điểm nhỏ vấn đề.\n\nBị người ám côn ném ở trong lao là cái gì cái tình huống.\n\nVì không trở thành cái thứ nhất nhậm chức chưa hoàn thành liền treo phế vật người chơi, Cố Tức lựa chọn tự cứu!\n\nKết quả, một vị tương lai được xưng là vong linh hơi thở cường giả, bước lên hắn con đường truyền kỳ.\n\n----------------', '2025-11-28 08:36:34', 'HoiThoVongLinh.jpg', 0, 0, 0, 0),
(12, 'Nhiên Tẫn Chi Long', '', 12, 8, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\nĐầu này Hồng Long theo mênh mông hoang nguyên mà đến, ngấp nghé thế gian hết thảy lực lượng, hắn từng có vô số danh hiệu: Quần Sơn nộ diễm, Luyện Ngục thiên tai, Hồng hoàng đế, Nhiên Tẫn chi long. . .\n\nThế giới tại cự long dưới bóng tối run rẩy, ma pháp cách mạng công nghiệp triều dâng bị hắn nhấc lên, tại hắn tạo dựng trong đế quốc, Phi Long rít gào, hỏa lực cùng vang lên, sắt thép cự thú xuất hiện ở trên trời cùng đại địa, lại có đến từ dị thế giới thiên tai bốn phía hoành hành', '2025-11-28 08:37:51', 'NhienTanChiLong.jpg', 0, 0, 0, 0),
(13, 'Phục Sơn Hà', '', 13, 5, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\nĐại Ngu, một triều đại trong lịch sử không có tồn tại qua.\n\nNhân vật chính một khi ngộ nhập trong đó, nhấc lên ầm ầm sóng dậy đại thời đại.\n\nTriều đình đánh cờ, hiển thị rõ ngươi lừa ta gạt.', '2025-11-28 08:39:17', 'PhucSonHa.jpg', 0, 0, 0, 0),
(14, 'Địa Trung Hải Bá Chủ Chi Lộ', '', 13, 5, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\n\"Một bộ đế quốc Bulgaria ra đời sử, một bộ Balkans nước nhỏ phấn đấu sử! Vai chính xuyên việt Ferdinand I, dẫn Bulgaria trỗi dậy đường.\n\nMột bộ hợp lý thôi diễn văn, không não tàn! Lịch sử đảng tất nhìn, ngoại quốc lịch sử người yêu thích càng là không cho bỏ qua!\"', '2025-11-28 08:40:34', 'DiaTrungHaiBaChuChiLo.jpg', 0, 0, 0, 0),
(15, 'Quốc Vương', '', 13, 9, 'Nếu thấy truyện hay bạn có thể chia sẻ với bạn bè hoặc thể hiện cảm xúc của mình bằng cách bình luận ở dưới.\n\nVăn án truyện:\nVạn tộc cùng tồn tại, kiếm cùng ma pháp hoà lẫn, Nhân tộc, Hải tộc, Tinh Linh, Ải Nhân, Thú Nhân... Giành trước nở rộ văn minh chi quang, viết lên sử thi bi ca!\n\n', '2025-11-28 08:41:35', 'QuocVuong.jpg', 0, 0, 0, 0);

